import React, { useEffect } from 'react';
import {useLocation} from 'react-router-dom'

function ResetScroll(props) {
    const {pathname} = useLocation()

    useEffect(() => {
        window.scrollTo(0, 0)
    }, [pathname])
    return null;
}

export default ResetScroll;
